import React, { useState, useEffect } from "react";
import "./Tweetbox.css";
import { Avatar, Button } from "@mui/material";
import AddPhotoAlternateOutlinedIcon from "@mui/icons-material/AddPhotoAlternateOutlined";
import axios from "axios";
import { useUserAuth } from "../../../context/UserAuthContext";
import useLoggedinuser from "../../../hooks/useLoggedinuser";

const Tweetbox = () => {
  const [post, setPost] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const { user } = useUserAuth();
  const [loggedinuser] = useLoggedinuser();
  const email = user?.email;
  const userProfilePic = loggedinuser[0]?.profileImage
    ? loggedinuser[0].profileImage
    : user && user.photoURL;

  useEffect(() => {
    if (user?.providerData[0]?.providerId === "password") {
      fetch(`http://localhost:5000/loggedinuser?email=${email}`)
        .then((res) => res.json())
        .then((data) => {
          setName(data[0]?.name || "");
          setUsername(data[0]?.username || "");
        })
        .catch((error) => console.error("Error fetching logged-in user:", error));
    } else {
      setName(user?.displayName);
      setUsername(email?.split("@")[0]);
    }
  }, [user, email]);

  const handleUploadImage = (e) => {
    setIsLoading(true);
    const image = e.target.files[0];
    const formData = new FormData();
    formData.set("image", image);

    axios
      .post(
        "https://api.imgbb.com/1/upload?key=b48ccca05a6f8a493e15fe0e4d526b31",
        formData
      )
      .then((res) => {
        setImageUrl(res.data.data.display_url);
        setIsLoading(false);
      })
      .catch((error) => {
        console.error("Error uploading image:", error);
        setIsLoading(false);
      });
  };

  const handleTweet = (e) => {
    e.preventDefault();

    if (!name || !username) {
      console.error("Name or username is missing");
      return;
    }

    const userPost = {
      profilephoto: userProfilePic,
      post: post,
      photo: imageUrl,
      username: username,
      name: name,
      email: email,
    };

    setPost("");
    setImageUrl("");

    fetch("http://localhost:5000/post", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(userPost),
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("Post created:", data);
      })
      .catch((error) => {
        console.error("Error posting tweet:", error);
      });
  };

  return (
    <div className="tweetBox">
      <form onSubmit={handleTweet}>
        <div className="tweetBox__input">
          <Avatar src={userProfilePic} />
          <input
            type="text"
            placeholder="What's happening?"
            onChange={(e) => setPost(e.target.value)}
            value={post}
            required
          />
        </div>
        <div className="imageIcon_tweetButton">
          <label htmlFor="image" className="imageIcon">
            {isLoading ? (
              <p>Uploading Image...</p>
            ) : (
              <p>{imageUrl ? "Image Uploaded" : <AddPhotoAlternateOutlinedIcon />}</p>
            )}
          </label>
          <input
            type="file"
            id="image"
            className="imageInput"
            onChange={handleUploadImage}
          />
          <Button className="tweetBox__tweetButton" type="submit">
            Tweet
          </Button>
        </div>
      </form>
    </div>
  );
};

export default Tweetbox;
